import BoardRequest from './request'
import * as boardCommands from './commands'
import boardTopics from './topics'

/**
* Board request.
* @extends {BoardRequest}
*/
class BoardCommand extends BoardRequest {
  /**
  * @param {Board}  board       Board instance.
  * @param {Board}  commandLine Command line, see {@link src/board/commands/index.js} for a list of implemented commands.
  * @param {Object} settings    See {@link src/board/settings.js~boardSettings}.request for defaults keys/values.
  * @param {Object} [settings.parseResponse = true] See {@link src/board/settings.js~boardSettings}.request for defaults keys/values.
  */
  constructor(board, commandLine, settings = {}) {
    // Normalize and split the command line
    commandLine = commandLine.trim().replace(/ +/g, ' ')

    let commandArgs = commandLine.split(' ')
    let commandName = commandArgs.shift().toLowerCase()

    // parse response by default
    if (settings.parseResponse === undefined) {
      settings.parseResponse = true
    }

    // response...
    let response = r => {
      let raw = r.trim()

      if (raw.startsWith('error:Unsupported command')) {
        throw new Error('Unsupported command "' + commandName + '".')
      }

      // alarm/error ?
      let test = raw.slice(0, 30).toLowerCase()

      // ! fire return empty string if laser module not enabled, even if state is not in alarm mode... !
      if (! raw.length && ['fire', 'play', 'progress', 'abort', 'suspend', 'resume'].includes(commandName)) {
        test = '!!'
      }

      if (test === '!!' || test.startsWith('alarm')) {
        if (! board.alarm) {
          board.alarm = true
          board.publish(boardTopics.STATE_ALARM)
        }

        throw new Error('Alarm! System probably halted.')
      }

      if (commandName === 'M999') {
        board.publish(boardTopics.STATE_CLEAR, test !== 'ok')
      }

      // response parser
      let parserName = commandName

      if (parserName.startsWith('config-')) {
        parserName = 'config_' + parserName.slice(7)
      }

      let responseParser = boardCommands['cmd_' + parserName]

      if (! responseParser && settings.parseResponse) {
        throw new Error('Sorry! The "' + commandName + '" command is not (yet) implemented.')
      }

      // return (parsed) response
      let args = [].concat(commandArgs) // make a copy

      return settings.parseResponse ? responseParser(r, args) : r
    }

    // call parent constructor
    super(board, Object.assign({}, board.settings.request, settings, {
      data   : commandLine + '\n',
      url    : 'command',
      method : 'POST',
      filters: { response }
    }))

    /**
    * Board instance.
    * @type {Board}
    * @protected
    */
    this.board = board

    /**
    * Command line.
    * @type {String}
    * @protected
    */
    this.commandLine = commandLine

    /**
    * Command name.
    * @type {String}
    * @protected
    */
    this.commandName = commandName

    /**
    * Command arguments.
    * @type {Array}
    * @protected
    */
    this.commandArgs = commandArgs
  }

  /**
  * Send the request and return a Promise.
  *
  * @return {Promise<RequestEvent>}
  */
  send() {
    if (this.commandName === 'break') {
      this.board.MRIMode = true
      this.board.publish(boardTopics.STATE_MRI)
      // Fix for break command never respond
      this.settings.timeout     = 5000 // 5 seconds
      this.settings.maxAttempts = 1
    }

    return super.send()
  }
}

// Exports
export default BoardCommand
export { BoardCommand }

/**
* Parse `abort` command response.
*
* ### on success
* ```
* return 'Aborted! Please turn any heaters off manually.'
* ```
* ### on error
* ```
* return Error: 'Not currently playing.'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L266
* @see https://github.com/Smoothieware/Smoothieware/blob/1f73659f0b0cb6142e395c3d4d7dcbaadbac870a/src/modules/utils/player/Player.cpp#L359
*/
export function cmd_abort(raw, args) {
  // raw response
  raw = raw.trim()

  if (raw.startsWith('Not currently playing')) {
    return new Error('Not currently playing.')
  }

  if (! raw.startsWith('Aborted playing or paused file')) {
    return new Error('Unknown response string.')
  }

  return 'Aborted! Please turn any heaters off manually.'
}

/**
* Calculate the Steinhart Hart coefficients for a thermistor.
*
* ### on success
* ```
* return {
*   "saved": false, // true if -s param provided, else false
*   "target": null, // termistor num if -s param provided, else null
*   "I"     : 0.000722376862540841,
*   "J"     : 0.000722376862540841,
*   "K"     : 0.000722376862540841
* }
* ```
* ### on error
* ```
* return Error: 'Usage: calc_thermistor [-s0] T1,R1,T2,R2,T3,R3.'
* return Error: 'Unknown response string.'
* return Error: 'Invalid input values.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {Object|Error}
* @see http://smoothieware.org/steinharthart
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#878
*/
export function cmd_calc_thermistor(raw, args) {
  raw = raw.trim()

  if (raw.startsWith('Usage: calc_thermistor')) {
    return new Error('Usage: calc_thermistor [-s0] T1,R1,T2,R2,T3,R3.')
  }

  let matches = raw.match(/Steinhart Hart coefficients: *I(.*)J(.*)K(.*)/)

  if (! matches) {
    return new Error('Unknown response string.')
  }

  matches.shift()

  matches = matches.map(match => parseFloat(match.trim()))

  if (matches.includes(NaN)) {
    return new Error('Invalid input values.')
  }

  let saved  = args[0].startsWith('-s')
  let target = (saved && args[0][2]) || null

  return { saved, target, I: matches[0], J: matches[0], K: matches[0] }
}

import { normalizePath } from '../util'

/**
* Get file content.
*
* ### on success
* ```
* return "Raw file contents as text."
* ```
* ### on error
* ```
* return Error: 'File not found "xxx".'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L331
*/
export function cmd_cat(raw, args) {
  let source = normalizePath(args[0] || '')

  if (raw.trim().startsWith('File not found')) {
    return new Error('File not found "' + source + '".')
  }

  return raw
}

import { normalizePath } from '../util'

/**
* Change current folder.
*
* ### on success
* ```
* return "ok"
* ```
* ### on error
* ```
* return Error: 'Could not open "xxx" directory.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L358
*/
export function cmd_cd(raw, args) {
  let source = normalizePath(args[0] || '')

  if (raw.trim().startsWith('Could not open directory')) {
    return new Error('Could not open "' + source + '" directory.')
  }

  return 'ok'
}

/**
* Parse `config_get` command response.
*
* ### on success
* ```
* return {
*   "source" : "sd", // or cache
*   "setting": "default_feed_rate",
*   "value"  : "4000"
* }
* ```
* ### on error
* ```
* return Error: 'Undefined "xxx" setting in "yyy".'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {Object|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L257
* @see https://github.com/Smoothieware/Smoothieware/blob/08e30d628e00333e274757bb6feac8b5e5541125/src/modules/utils/configurator/Configurator.cpp#L30
* @see https://github.com/Smoothieware/Smoothieware/blob/08e30d628e00333e274757bb6feac8b5e5541125/src/libs/Config.cpp#L31
*/
export function cmd_config_get(raw, args) {
  raw = raw.trim()

  let matches = raw.match(/(.*): (.*) is not in config/)

  if (matches) {
    let source  = matches[1].trim()
    let setting = matches[2].trim()

    if (source === 'cached') {
      source = 'cache'
    }

    return new Error('Undefined "' + setting + '" setting in "' + source + '".')
  }

  matches = raw.match(/(.*): (.*) is set to (.*)/)

  if (! matches) {
    return new Error('Unknown response string.')
  }

  let source = matches[1].trim()

  if (source === 'cached') {
      source = 'cache'
    }

  return {
    source,
    setting: matches[2].trim(),
    value  : matches[3].trim()
  }
}

/**
* Parse `config_load` command response.
*
* ### on success
* ```
* return '...'
* ```
* ### on error
* ```
* return Error: 'Usage: config-load load|unload|dump|checksum'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L263
* @see https://github.com/Smoothieware/Smoothieware/blob/08e30d628e00333e274757bb6feac8b5e5541125/src/modules/utils/configurator/Configurator.cpp#L106
*/
export function cmd_config_load(raw, args) {
  raw = raw.trim()

  if (raw.startsWith('unsupported option:')) {
    return new Error('Usage: config-load load|unload|dump|checksum')
  }

  if (raw.startsWith('config cache loaded') || raw.startsWith('config cache unloaded')) {
    return true
  }

  let matches = raw.match(/^checksum of (.*) = (.*)/)

  if (matches) {
    return {
      setting : matches[1].trim(),
      checksum: matches[2].trim()
    }
  }

  let config = []
  let lines  = raw.split('\n')

  lines.forEach(line => {
    line    = line.trim()
    matches = line.match(/^([0-9]+) - (.*) : '(.*)' - found: (.*), default: (.*), default-double: (.*), default-int: (.*)/)

    if (! matches) {
      return new Error('Unknown response string.')
    }

    config.push({
      checksum     : matches[2],
      value        : matches[3],
      found        : matches[4],
      default      : matches[5],
      defaultDouble: matches[6],
      defaultInt   : matches[7]
    })
  })

  if (config.length) {
    return config
  }

  return new Error('Unknown response string.')
}

/**
* Parse `config_set` command response.
*
* ### on success
* ```
* return return {
*   "source" : "sd",
*   "setting": "default_feed_rate",
*   "value"  : "4000"
* }
* ```
* ### on error
* ```
* return Error: 'Usage: config-set source setting value.'
* return Error: 'Undefined source "xxx".'
* return Error: 'Not enough space in "xxx" to overwrite "yyy" setting.'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L260
* @see https://github.com/Smoothieware/Smoothieware/blob/08e30d628e00333e274757bb6feac8b5e5541125/src/modules/utils/configurator/Configurator.cpp#L68
*/
export function cmd_config_set(raw, args) {
  raw = raw.trim()

  if (raw.startsWith('Usage: config-set')) {
    return new Error('Usage: config-set source setting value')
  }

  let matches = raw.match(/(.*): (.*) has been set to (.*)/)

  if (matches) {
    return {
      source : matches[1].trim(),
      setting: matches[2].trim(),
      value  : matches[3].trim()
    }
  }

  matches = raw.match(/(.*) source does not exist/)

  if (matches) {
    return new Error('Undefined source "' + matches[1] + '".')
  }

  matches = raw.match(/(.*): (.*) not enough space/)

  if (matches) {
    return new Error('Not enough space in "' + matches[1].trim() + '" to overwrite "' + matches[2].trim() + '" setting.')
  }

  return new Error('Unknown response string.')
}

/**
* Parse `dfu` command response.
*
* ### on success
* ```
* return 'Entering boot mode...'
* ```
* ### on error
* ```
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L661
*/
export function cmd_dfu(raw, args) {
  raw = raw.trim()

  if (raw !== 'Entering boot mode...') {
    return new Error('Unknown response string.')
  }

  return raw
}

/**
* Fire laser.
*
* ! fire return empty string if laser module not enabled, even if state is not in alarm mode... !
* This parser will never called in this case...
*
* ### on success
* ```
* return "..."
* ```
* ### on error
* ```
* return Error: 'Usage: Usage: fire power|off.'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L269
* @see https://github.com/Smoothieware/Smoothieware/blob/dcf0252ef5d048f22949c07224e10cc8dde341cc/src/modules/tools/laser/Laser.cpp#L134
*/
export function cmd_fire(raw, args) {
  raw = raw.trim()

  if (raw.startsWith('Usage: fire power')) {
    return new Error('Usage: fire power|off.')
  }

  if (raw.startsWith('turning laser off')) {
    return { fire: false, power: 0 }
  }

  // WARNING: Firing laser at %1.2f%% power, entering manual mode use fire off to return to auto mode
  let matches = raw.match(/^WARNING: Firing laser at ([0-9\.]+)%/)

  if (! matches) {
    return new Error('Unknown response string.')
  }

  return { fire: true, power: parseInt(matches[1]) }
}

/**
* Get forward kinematics.
*
* ### on success
* ```
* return {
*   "move": false,
*   "x"   : 10,
*   "y"   : 10,
*   "z"   : 10
* }
* ```
* ### on error
* ```
* return Error: 'Usage: get ik [-m] x[,y,z].'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {Object|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#771
*/
export function get_fk(raw, args) {
  let move = args.shift() === '-m'

  // error
  if (raw.startsWith('error:')) {
    return new Error('Usage: get fk [-m] x[,y,z].')
  }

  let matches = raw.match(/cartesian= X ([0-9\.]+), Y ([0-9\.]+), Z ([0-9\.]+)/)

  if (! matches) {
    return new Error('Unknown response string.')
  }

  return {
    move,
    x: parseFloat(matches[1]),
    y: parseFloat(matches[2]),
    z: parseFloat(matches[3])
  }
}

/**
* Get inverse kinematics.
*
* ### on success
* ```
* return {
*   "move": false,
*   "x"   : 10,
*   "y"   : 10,
*   "z"   : 10
* }
* ```
* ### on error
* ```
* return Error: 'Usage: get ik [-m] x[,y,z].'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {Object|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#771
*/
export function get_ik(raw, args) {
  let move = args.shift() === '-m'

  // error
  if (raw.startsWith('error:')) {
    return new Error('Usage: get ik [-m] x[,y,z].')
  }

  let matches = raw.match(/actuator= X ([0-9\.]+), Y ([0-9\.]+), Z ([0-9\.]+)/)

  if (! matches) {
    return new Error('Unknown response string.')
  }

  return {
    move,
    x: parseFloat(matches[1]),
    y: parseFloat(matches[2]),
    z: parseFloat(matches[3])
  }
}

/**
* Get position.
*
* ### on success
* ```
* return {
*   "WCS": {
*     "command"    : "M114",
*     "description": "Position of all axes",
*     "x"          : 0,
*     "y"          : 0,
*     "z"          : 0
*   },
*   "WPOS": {
*     "command"    : "M114.1",
*     "description": "Real time position of all axes"
*     // , x, y, z
*   },
*   "MPOS": {
*     "command"    : "M114.2",
*     "description": "Real time machine position of all axes"
*     // , x, y, z
*   },
*   "APOS": {
*     "command"    : "M114.3",
*     "description": "Real time actuator position of all actuators"
*     // , x, y, z
*   },
*   "LMS": {
*     "command"    : "M114.4",
*     "description": "Last milestone"
*     // , x, y, z
*   },
*   "LMP": {
*     "command"    : "M114.5",
*     "description": "Last machine position"
*     // , x, y, z
*   }
* }
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {Object|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#818
*/
export function get_pos(raw, args) {
  // last C: X:0.0000 Y:0.0000 Z:0.0000
  // realtime WPOS: X:0.0000 Y:0.0000 Z:0.0000
  // MPOS: X:0.0000 Y:0.0000 Z:0.0000
  // APOS: X:0.0000 Y:0.0000 Z:0.0000
  // LMS: X:0.0000 Y:0.0000 Z:0.0000
  // LMP: X:0.0000 Y:0.0000 Z:0.0000
  // split on new lines
  let lines = raw.split('\n')
  let key, positions = {}

  lines.forEach(line => {
    let r = parsePositionString(line)
    positions[r.key] = r.position
  })

  // split response text on new lines
  return positions
}

// Parse position string.
// WCS : M114   - Last WCS.
// WPOS: M114.1 - Realtime WCS.
// MPOS: M114.2 - Realtime machine coordinate system.
// APOS: M114.3 - Realtime actuator position.
// LMS : M114.4 - Last milestone.
// LMP : M114.5 - Last machine position.
function parsePositionString(line) {
  let parts  = line.split(':')
  let key    = parts.shift().replace(' ', '_').toUpperCase()
  let coords = parts.join(':').trim().split(' ')

  let position = {
    x  : parseFloat(coords[0].substr(2)),
    y  : parseFloat(coords[1].substr(2)),
    z  : parseFloat(coords[2].substr(2))
  }

  if (key === 'LAST_C') {
    key = 'WCS'
  }
  else if (key === 'REALTIME_WPOS') {
    key = 'WPOS'
  }

  switch (key) {
    case 'WCS':
      position.command     = 'M114'
      position.description = 'Position of all axes'
      break
    case 'WPOS':
      position.command     = 'M114.1'
      position.description = 'Real time position of all axes'
      break
    case 'MPOS':
      position.command     = 'M114.2'
      position.description = 'Real time machine position of all axes'
      break
    case 'APOS':
      position.command     = 'M114.3'
      position.description = 'Real time actuator position of all actuators'
      break
    case 'LMS':
      position.command     = 'M114.4'
      position.description = 'Last milestone'
      break
    case 'LMP':
      position.command     = 'M114.5'
      position.description = 'Last machine position'
      break
    default:
      position.command     = 'M114.?'
      position.description = 'Unknown type'
  }

  return { key, position }
}

