import { normalizePath } from '../util'

/**
* Parse `mkdir` command response.
*
* ### on success
* ```
* return '/sd/new_directory'
* ```
* ### on error
* ```
* return Error: 'Could not create directory "xxx".'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L349
*/
export function cmd_mkdir(raw, args) {
  raw = raw.trim()

  let source = normalizePath(args[0] || '')

  if (raw.startsWith('could not create directory')) {
    return new Error('Could not create directory "' + source + '".')
  }

  if (! raw.startsWith('created directory')) {
    return new Error('Unknown response string.')
  }

  return source
}

import { normalizePath } from '../util'

/**
* Move a file.
*
* ### on success
* ```
* return {
*   source: '/sd/source.gcode',
*   target: '/sd/target.gcode'
* }
* ```
* ### on error
* ```
* return Error: 'Could not move "xxx" to "yyy".'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {Object|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L339
*/
export function cmd_mv(raw, args) {
  // raw response
  raw = raw.trim()

  let source = normalizePath(args[0] || '')
  let target = normalizePath(args[1] || '')

  // file not found
  if (raw.startsWith('Could not rename')) {
    return new Error('Could not move "' + source + '" to "' + target + '".')
  }

  return { source, target }
}

/**
* Get network config.
*
* ### on success
* ```
* return {
*   "ip"     : "192.168.1.102",
*   "gateway": "192.168.1.1",
*   "mask"   : "255.255.255.0",
*   "mac"    : "00:00:00:00:00:00"
* }
* ```
* ### on error
* ```
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {Object|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#616
*/
export function cmd_net(raw, args) {
  let matches = raw.trim().match(/IP Addr:([^\n]+)\nIP GW:([^\n]+)\nIP mask:([^\n]+)\nMAC Address:([^\n]+)/)

  if (! matches) {
    return new Error('Unknown response string.')
  }

  return {
    ip     : matches[1].trim(),
    gateway: matches[2].trim(),
    mask   : matches[3].trim(),
    mac    : matches[4].trim()
  }
}

/**
* Parse `ok` command response.
*
* ### on success
* ```
* return "ok"
* ```
* ### on error
* ```
* return Error: 'ko'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L272
*/
export function cmd_ok(raw, args) {
  if (raw.trim() === 'ok') {
    return 'ok'
  }

  return new Error('ko')
}

import { normalizePath } from '../util'

/**
* Parse `play` command response.
*
* ### on success
* ```
* return {
*   file: "/sd/my_file.gcode",
*   size: 8600 // or null if not computable
* }
* ```
* ### on error
* ```
* return Error: 'File "xxx" not found.'
* return Error: 'Currently printing, abort print first.'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {Object|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L266
* @see https://github.com/Smoothieware/Smoothieware/blob/1f73659f0b0cb6142e395c3d4d7dcbaadbac870a/src/modules/utils/player/Player.cpp#L267
*/
export function cmd_play(raw, args) {
  // raw response
  raw = raw.trim()

  // file file
  let file = normalizePath(args[0] || '')

  if (raw.startsWith('File not found')) {
    return new Error('File "' + file + '" not found.')
  }

  if (raw.startsWith('Currently printing')) {
    return new Error('Currently printing, abort print first.')
  }

  if (! raw.startsWith('Playing')) {
    return new Error('Unknown response string.')
  }

  let lines = raw.split('\n')
  let size  = parseInt(lines[1].trim().split(' ').pop())

  return { file, size }
}

/**
* Parse `progress` command response.
*
* ### on success
* ```
* return {
*   "paused"   : false,
*   "file"     : "/sd/file.gcode",
*   "complete" : 85, // percent
*   "elapsed"  : "00:13:43",
*   "estimated": "00:02:14" // undefined if elapsed <= 10 seconds
* }
*
* // or if paused
* return {
*   "paused": true,
*   "played": 150,
*   "total" : 380
* }
* ```
* ### on error
* ```
* return Error: 'Not currently playing.'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {Object|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L266
* @see https://github.com/Smoothieware/Smoothieware/blob/1f73659f0b0cb6142e395c3d4d7dcbaadbac870a/src/modules/utils/player/Player.cpp#L315
*/
export function cmd_progress(raw, args) {
  // raw response
  raw = raw.trim()

  if (raw.startsWith('Not currently playing')) {
    return new Error('Not currently playing.')
  }

  if (raw.startsWith('SD print is paused at')) {
    let matches = raw.match(/SD print is paused at ([0-9]+)\/([0-9]+)/)

    if (! matches) {
      return new Error('Unknown response string.')
    }

    return {
      paused: true,
      played: parseInt(matches[1]),
      total : parseInt(matches[2])
    }
  }

  // file: /sd/skate.gcode, 27 % complete, elapsed time: 7 s
  // file: /sd/skate.gcode, 27 % complete, elapsed time: 13 s, est time: 33 s

  // Since -> https://github.com/Smoothieware/Smoothieware/commit/7f7d84293841e659d436b8f833e90d6f8cbdf281
  // file: /sd/tag.gcode, 6 % complete, elapsed time: 00:01:42, est time: 00:25:23
  let matches = raw.match(/file: ([^,]+), ([0-9]+) % complete, elapsed time: ([0-9\:]+)(?: s)?(?:, est time: ([0-9\:]+)( s)?)?/)

  if (! matches) {
    return new Error('Unknown response string.')
  }

  // https://github.com/Smoothieware/Smoothieware/commit/7f7d84293841e659d436b8f833e90d6f8cbdf281
  let elapsed   = matches[3]
  let estimated = matches[4]

  if (matches[5]) {
    elapsed   = timeFormat(elapsed)
    estimated = timeFormat(estimated)
  }

  return {
    paused   : false,
    file     : matches[1],
    complete : parseInt(matches[2]),
    elapsed,
    estimated
  }
}

// seconds -> 00:00:00
function timeFormat(time) {
  time = parseInt(time)
  return [time / 3600, (time % 3600) / 60, time % 60].join(':')
}

/**
* Get current working directory.
*
* ### on success
* ```
* return "/sd/folder"
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L373
*/
export function cmd_pwd(raw, args) {
  return raw.trim()
}

/**
* Remount SD card.
*
* ### on success
* ```
* return "remounted"
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#324
*/
export function cmd_remount(raw, args) {
  return raw.trim()
}

/**
* Reset the board.
*
* ### on success
* ```
* return "Smoothie out. Peace. Rebooting in 5 seconds...\r\n"
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L647
*/
export function cmd_reset(raw, args) {
  return raw.trim()
}

/**
* Parse `resume` command response.
*
* ### on success
* ```
* return [
*   "Restoring saved XYZ positions and state...",
*   "Resuming print",
*   ...
* ]
* ```
* ### on error
* ```
* return Error: 'Not suspended.'
* return Error: 'Resume aborted by kill.'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L266
* @see https://github.com/Smoothieware/Smoothieware/blob/1f73659f0b0cb6142e395c3d4d7dcbaadbac870a/src/modules/utils/player/Player.cpp#L594
*/
export function cmd_resume(raw, args) {
  raw = raw.trim()

  if (raw.startsWith('Not suspended')) {
    return new Error('Not suspended.')
  }

  if (! raw.startsWith('resuming print...')) {
    return new Error('Unknown response string.')
  }

  if (raw.indexOf('Resume aborted by kill') !== -1) {
    return new Error('Resume aborted by kill.')
  }

  let lines = raw.split('\n').map(line => line.trim())

  lines.shift()

  return lines
}

import { normalizePath } from '../util'

/**
* Remove a file.
*
* ### on success
* ```
* return "/sd/removed_file.gcode"
* ```
* ### on error
* ```
* return Error: 'Could not remove "xxx".'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L331
*/
export function cmd_rm(raw, args) {
  // raw response
  raw = raw.trim()

  let source = normalizePath(args[0] || '')

  // file not found
  if (raw.startsWith('Could not delete')) {
    return new Error('Could not remove "' + source + '".')
  }

  return source
}

/**
* Parse `save` command response.
*
* ### on success
* ```
* return {
*   stored: true,
*   file  : '/sd/config-override'
* }
* ```
* ### on error
* ```
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L542
*/
export function cmd_save(raw, args) {
  raw = raw.trim()

  let matches = raw.match(/^Settings Stored to (.*)/)

  if (! matches) {
    return new Error('Unknown response string.')
  }

  return { stored: true, file: matches[1] }
}

/**
* Set temperature.
*
* - set_temp [bed|hotend]
*
* ### on success
* ```
* return {
*   "device": "bed",
*   "target": 180
* }
* ```
* ### on error
* ```
* return Error: '"xxx" is not a known temperature device.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {Object|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#857
*/
export function cmd_set_temp(raw, args) {
  // raw response
  raw = raw.trim()

  // device
  let device = (args.shift() || '').toLowerCase()

  // error
  if (raw.endsWith('is not a known temperature device')) {
    return new Error('"' + device + '" is not a known temperature device.')
  }

  // new temperature
  let target = parseInt(args.shift() || 0)

  return { device, target }
}

/**
* Parse `suspend` command response.
*
* ### on success
* ```
* return 'Suspending print, waiting for queue to empty...'
* ```
* ### on error
* ```
* return Error: 'Already suspended.'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {String|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L266
* @see https://github.com/Smoothieware/Smoothieware/blob/1f73659f0b0cb6142e395c3d4d7dcbaadbac870a/src/modules/utils/player/Player.cpp#L359
*/
export function cmd_suspend(raw, args) {
  // raw response
  raw = raw.trim()

  if (raw.startsWith('Already suspended')) {
    return new Error('Already suspended.')
  }

  if (! raw.startsWith('Suspending print')) {
    return new Error('Unknown response string.')
  }

  return 'Suspending print, waiting for queue to empty...'
}

/**
* Switch module.
*
* `switch fan on` or `switch fan 150`
*
* ### on success
* ```
* return {
*   "device": "fan",
*   "value" : "on"
* }
* ```
* ### on error
* ```
* return Error: '"xxx" is not a known switch device.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {Object|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#L916
*/
export function cmd_switch(raw, args) {
  // raw response
  raw = raw.trim()

  // device
  let device = args.shift() || ''

  // error
  if (raw.endsWith('is not a known switch device')) {
    return new Error('"' + device + '" is not a known switch device.')
  }

  return { device, value: args.shift() || '' }
}

/**
* Parse "test circle" response.
*
* ### on success
* ```
* return [gcode, gcode, ..., 'done']
* ```
* ### on error
* ```
* return Error: 'Usage: test circle radius iterations [feedrate]'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {Object|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#989
*/
export function test_circle(raw, args) {
  if (raw.startsWith('error:')) {
    return new Error('Usage: test circle radius iterations [feedrate]')
  }

  return raw.split('\n')
}

/**
* Parse "test jog" response.
*
* ### on success
* ```
* return [gcode, gcode, ..., 'done']
* ```
* ### on error
* ```
* return Error: 'Usage: test jog axis distance iterations [feedrate]'
* return Error: 'Unknown response string.'
* ```
* @param  {String}   raw  Raw command response string.
* @param  {String[]} args Command arguments.
* @return {Object|Error}
* @see https://github.com/Smoothieware/Smoothieware/blob/d79254323f4bb951426c6add29a4451130eaa018/src/modules/utils/simpleshell/SimpleShell.cpp#962
*/
export function test_jog(raw, args) {
  if (raw.startsWith('error:')) {
    return new Error('Usage: test jog axis distance iterations [feedrate]')
  }

  return raw.split('\n')
}

