/**
* Board folder class.
*/
class BoardFolder {
  /**
  * @param {Object} folder Folder object from `ls -s` command.
  */
  constructor(folder) {
    /**
    * Parent folder path `/sd`.
    * @type {String}
    */
    this.parent = file.parent

    /**
    * Folder size (bytes).
    * @type {Integer}
    */
    this.size = file.size

    /**
    * Folder path `/sd/my_folder`.
    * @type {String}
