function on(event) {
  console.info('on:', event.type, event);
}

new Request({
  url   : 'http://192.168.1.102/command',
  method: 'POST',
  data  : 'version\n',
  on    : {
    'retry'            : on,
    'retry.send'       : on,
    'retry.limit'      : on,

    'download.load'    : on,
    'download.error'   : on,
    'download.abort'   : on,
    'download.timeout' : on,
    'download.progress': on,

    'upload.load'      : on,
    'upload.error'     : on,
    'upload.abort'     : on,
    'upload.timeout'   : on,
    'upload.progress'  : on
  },
  filters: {
    status  : status => status === 200,
    response: response => JSON.stringify(response)
  }
})
.send()
.then(event => {
  console.info('then:', event);
})
.catch(event => {
  console.error('catch:', event);
});
